<?php/** * Functions * * @author Your Inspiration Themes * @package YITH Newsletter Popup * @version 1.0.0 */if ( !defined( 'YITH_NEWSLETTER_POPUP' ) ) { exit; } // Exit if accessed directlyif( !function_exists( 'yith_newsletter_popup_is_enabled' ) ) {    /**     * Returns if the plugin is enabled     *     * @return bool     * @since 1.0.0     */    function yith_newsletter_popup_is_enabled() {        return get_option('yith_newsletter_popup_enable') == '1';    }}